  /* Only try to remove the element and update the record if it was found, i.e. not -1 */
  if(elementIndex > -1) {
    listArray.splice(elementIndex,1);
    listGr.setValue(listField,listArray);
    listGr.update();
  }
  /* Reset the initial array related variables */
  listArray = [];
  elementIndex = -1;
}

