      obj.priority = getIncRec.getDisplayValue("priority");
      obj.link =
        "sp?id=form&table=incident&sys_id=" + getIncRec.getUniqueValue();
      data.tableRecord.push(obj);
    }
  } catch (e) {
    gs.addErrorMessage("Error Catched" + e);
  }
})();

