                gs.debug('Customer updates application (sys_update_xml.application): ' + grCustomerUpdates.getDisplayValue('application'));
                gs.debug('-------------------------------------------------------------------------------------------------------------');
            
                distinctXMLList.push(grSysUpdateSet.getValue('name'));
            }
        }
        
    }

