    listGR.addNullQuery('parent');
    listGR.query();
    while(listGR.next()){
        var list = {};
        list.sys_id = listGR.getUniqueValue();
        list.name = listGR.getValue('name');
        list.elements = getElements(listGR.getUniqueValue(), fieldsToAdd);
