            if (gs.nil(incidentRecord.priority) || incidentRecord.priority < 1 || incidentRecord.priority > 5) {
                errors.push('Priority must be between 1 and 5.');
            }
        } else {
            errors.push('Field "priority" does not exist on the record.');
        }

        return errors;
