        }
        ga.setGroup(false);
        //ga.setOrder(false);
        ga.addAggregate(pAggregateType, pColumn);
        ga.query();
        if (ga.next()) {
            return ga.getAggregate(pAggregateType, pColumn);
        } else {
            return null;
        }
