        var encodedQuery = global.JSUtil.nil(in_encodedQuery) ? this.getParameter('sysparm_encodedQuery') : in_encodedQuery;
        return {
            tableName: tableName,
            fieldName: fieldName,
            encodedQuery: encodedQuery
        };
