var DateUtils = Class.create();
DateUtils.prototype = {
    initialize: function () { },

    getMonths: function (startDate, endDate) {

        var MONTHS_IN_A_YEAR = 12;
        var totalMonths = 0;

        var startDateGd = new GlideDate();
        startDateGd.setValue(startDate);
        var startDateTimeGdt = new GlideDateTime(startDateGd);
        var startDateMonth = startDateTimeGdt.getMonthLocalTime();
        var startDateYear = startDateTimeGdt.getYearLocalTime();

        var endDateGt = new GlideDate();
        endDateGt.setValue(endDate);
        var endDateTimeGdt = new GlideDateTime(endDateGt);
        var endDateMonth = endDateTimeGdt.getMonthLocalTime();
        var endDateYear = endDateTimeGdt.getYearLocalTime();

        if (startDateYear != endDateYear) {
            totalMonths = MONTHS_IN_A_YEAR - startDateMonth + endDateMonth + (endDateYear - startDateYear - 1) * MONTHS_IN_A_YEAR;
        } else {
            totalMonths = endDateMonth - startDateMonth;
        }

        return totalMonths;

    },

    type: 'DateUtils'
};

var FinancialServiceUtilities = Class.create();
FinancialServiceUtilities.prototype = {
    initialize: function() {
    },

    /**
     * Calculates simple interest.
     *
     * @param {number} principal - The principal amount.
     * @param {number} rate - The annual interest rate (in percentage).
     * @param {number} time - The time period in years.
     * @returns {number} The calculated simple interest.
     */
    calculateInterest: function(principal, rate, time) {
        // Simple interest calculation
        return (principal * rate * time) / 100;
    },

    /**
     * Calculates compound interest.
     *
     * @param {number} principal - The principal amount.
     * @param {number} rate - The annual interest rate (in percentage).
     * @param {number} time - The time period in years.
     * @param {number} compoundingFrequency - The number of times interest is compounded per year.
     * @returns {number} The calculated compound interest.
     */
    calculateCompoundInterest: function(principal, rate, time, compoundingFrequency) {
        // Compound interest calculation
        var compoundInterest = principal * Math.pow(1 + (rate / compoundingFrequency), compoundingFrequency * time);
        return compoundInterest - principal;
    },

    /**
     * Formats a currency amount.
     *
     * @param {number} amount - The amount to be formatted.
     * @param {string} currencyCode - The currency code (e.g., "USD", "EUR").
     * @returns {string} The formatted currency amount.
     */
    formatCurrency: function(amount, currencyCode) {
        // Format the amount as currency
        return gs.formatNumber(amount, currencyCode);
    },

    /**
     * Calculates monthly loan payments.
     *
     * @param {number} principal - The principal loan amount.
     * @param {number} rate - The annual interest rate (in percentage).
     * @param {number} term - The loan term in years.
     * @returns {number} The calculated monthly loan payment.
     */
    calculateLoanPayments: function(principal, rate, term) {
        // Calculate monthly loan payments (assuming monthly compounding)
        var monthlyInterestRate = rate / 12 / 100;
        var numberOfPayments = term * 12;
        var monthlyPayment = principal * monthlyInterestRate * Math.pow(1 + monthlyInterestRate, numberOfPayments) / (Math.pow(1 + monthlyInterestRate, numberOfPayments) - 1);
        return monthlyPayment;
    },

    type: 'FinancialServiceUtilities'
};

var EXT_TableUtils = Class.create();

EXT_TableUtils.prototype = Object.extendsObject(TableUtils, {

    initialize: function(tableName) {
        TableUtils.prototype.initialize.call(this, tableName);        
    },

    /**SNDOC
        @name getFieldsAndAttributes
        @description Returns a data structure with field name and field properties for a given table.
        OOB getFields() methods from either GlideRecord() or GlideRecordUtil()
        only work with an existing record and not just with the table name. This one goes
        to sys_dictionary directly and therefore does not need a valid GlideRecord to work.
        The returned object has this structure:
        {
            <field_name_1>: {
               field_label: <label>,
               field_size: <size>,
               field_type: <type>,
               reference_table: <table> (only for reference or glide_list types)
            },
            <field_name_2>: {
               ...
            }
        }
        @example
        var fields = new EXT_TableUtils('incident').getFieldsAndAttributes();
        for (var fieldName in fields) {
            gs.print('Field ' + fieldName + ' is of type ' + fields[fieldName].field_type);
        }
        @returns {object} [fields]
        */

    getFieldsAndAttributes: function() {

        var fields = {};

        // Get all the table names in the hierarchy and turn it into an array
        // getHierarchy() is a method from the parent class TableUtils

        var tableHierarchy = this.getHierarchy(this.tableName);
        
        // Go find all the fields for all the tables of the hierarchy

        var dicGr = new GlideRecord('sys_dictionary');

        dicGr.addQuery('name', 'IN', j2js(tableHierarchy).join(','));       
        dicGr.addEncodedQuery('internal_type!=collection^ORinternal_type=NULL');
        dicGr.query();

        while (dicGr.next()) {

            var fieldName = dicGr.getValue('element');

            fields[fieldName] = {};
            fields[fieldName].field_label = dicGr.getValue('column_label');
            fields[fieldName].field_size = dicGr.getValue('max_length');

            fields[fieldName].field_type = dicGr.getValue('internal_type');
            if (fields[fieldName].field_type === 'reference' || fields[fieldName].field_type === 'glide_list') {
                fields[fieldName].reference_table = dicGr.getValue('reference');
            }
            
        }

        return fields;

    },

    type: 'EXT_TableUtils'

});

