    }
  }
}

function recursive(id) {
  ci.push(id);

  var gr = new GlideRecord('cmdb_rel_ci');
  gr.addQuery('child', id);
  gr.query();
  while (gr.next()) {
    recursive(gr.parent.toString());
  }

  return ci;
}
